import React from 'react'
import { View, Text, FlatList, Image, StyleSheet } from 'react-native';
import { useSelector } from 'react-redux';
import dummyData from '../userData.json';


const FriendListScreen = () => {
  // const usersOnline = useSelector(state => state.usersOnline);
  const usersOnline = dummyData; // dummy data for testing

  useSelector(state => state.usersOnline);


  console.log('usersOnline', usersOnline);
  return (
    <View style={{flex: 1, alignItems: 'center', justifyContent: 'center'}}>
      <FlatList
        data={usersOnline}
        renderItem={({item}) => {
          console.log('item', item);
          return (
            <View style={styles.itemsContainer}>
              <Image style={styles.avatarImage} source= {{ uri: item.avatar }}/>
              {item.nextRound ? (
                <View style={ styles.avatarContaine }>
                  <Text style={ styles.text }>{item.username}</Text>
                  <Text style={ styles.text }>{item.drink}</Text>
                  <Text style={ styles.text }>{item.isFinished}</Text>
                </View>
              ) : (
              <View style={ styles.avatarContainerNextRound }>
                <Text style={ styles.text }>{item.username}</Text>
                <Text style={ styles.text }>{item.drink}</Text>
                <Text style={ styles.text }>{item.isFinished}</Text>
              </View>
              )}
            </View>
          );
        }}
        keyExtractor={item => item.userId}
      />
    </View>
  )
}

export default FriendListScreen

const styles = StyleSheet.create({
  itemsContainer: {
    flex: 1,
    flexDirection: 'row'
  },
  avatarImage: {
    width: 75,
    height: 75,
  },
  avatarContainer: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  avatarContainerNextRound: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: 'teal'
  },
  text: {
    fontSize: 12,
    paddingHorizontal: 10
  }
})
